import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllBlogs } from "../Actions/blogActions";
import { loginUser } from "../Actions/authActions";

class Blogs extends Component {
  state = {
    blogs: null
  };

  componentDidMount() {
    if (localStorage.jwtToken) {
      let Object = localStorage.getItem("jwtToken");
      Object = JSON.parse(Object);
      // console.log(Object);

      const obj = {
        email: Object.email,
        password: Object.password
      };

      this.props.loginUser(obj);
    }
    this.props.getAllBlogs();
  }
  componentWillReceiveProps(next) {
    this.setState({ blogs: next.blog.blogs });
  }

  NotAuth = i => {
    if (this.state.blogs == null) {
      return <div>LOADING...</div>;
    }
  };
  render() {
    let { blogs } = this.props.blog;

    const blogStyle = {
      padding: "10px",
      border: "5px solid #d9534f",
      margin: "0",
      backgroundColor: "white"
    };

    const textarea = {
      backgroundColor: "#d9d6d2",
      fontSize: "20px"
    };

    const color = {
      color: "red"
    };

    return (
      <div className="container">
        <h1>All Blogs</h1>
        <br />
        {this.NotAuth()}
        {this.props.auth.isAuthenticated
          ? this.props.blog.blogs.map(blog => {
              // console.log(blog);

              return (
                <div>
                  <div style={blogStyle} className="row">
                    <div className="col-md-3">
                      <img src={require("./i.png")}></img>
                      <br />
                      <br />
                      {blog.user.name}
                    </div>
                    <div className="col-md-9">{blog.blogContent} </div>

                    <br />
                    <textarea
                      className="form-control"
                      style={textarea}
                      placeholder="Add a Comment"
                    ></textarea>
                    <br />
                    <button className="btn btn-danger">Comment</button>
                    <button className="btn">
                      <span style={color}>View All Comments</span>
                    </button>
                  </div>
                  <br />
                  <br />
                </div>
              );
            })
          : ""}
      </div>
    );
  }
}

const mapStatetoProps = state => ({
  auth: state.auth,
  blog: state.blog,
  errors: state.errors
});

export default connect(
  mapStatetoProps,
  { getAllBlogs, loginUser }
)(Blogs);
