import axios from "axios";

export const newBlog = blog => dispatch => {
  axios
    .post("/blog", blog)
    .then(data => {
      history.push("/blogs");
    })
    .catch(err => {
      console.log(err);
      dispatch({
        type: "GET_ERRORS",
        payload: err.response.data
      });
    });
};
