import React, { Component } from "react";
import { getComments } from "../Actions/blogActions";
import { loginUser } from "../Actions/authActions";
import { connect } from "react-redux";

class Comments extends Component {
  state = {
    blog: null
  };

  componentWillReceiveProps(next) {
    console.log(next.blog);

    this.setState({ blog: next.blog.blog });
  }
  NotAuth = () => {
    if (this.state.blogs == null) {
      return <div>LOADING...</div>;
    }
  };

  componentDidMount() {
    // if (localStorage.jwtToken) {
    //   let Object = localStorage.getItem("jwtToken");
    //   Object = JSON.parse(Object);
    //   // console.log(Object);

    //   const obj = {
    //     email: Object.email,
    //     password: Object.password
    //   };

    //   this.props.loginUser(obj);
    // }
    this.props.getComments(this.props.match.params.id);
  }

  render() {
    // const { comments } = this.props.blog;
    const commentss = {
      padding: "10px",
      border: "5px solid #d9534f",
      margin: "0",
      backgroundColor: "white"
    };

    return (
      <div className="container">
        {/* {this.NotAuth()} */}
        {this.state.blog === null
          ? this.NotAuth()
          : this.state.blog.comments.map(comment => {
              return (
                <div style={commentss}>
                  {comment.text}
                  <div align="right">
                    <i>Comment by - </i>
                    <b>Madhav Khurana</b>
                  </div>
                </div>
              );
            })}
      </div>
    );
  }
}

const mapStatetoProps = state => ({
  auth: state.auth,
  blog: state.blog,
  errors: state.errors
});

export default connect(
  mapStatetoProps,
  { loginUser, getComments }
)(Comments);
