import axios from "axios";

export const newBlog = blog => dispatch => {
  axios
    .post("/blog", blog)
    .then(res => res.json(blog))
    .catch(err => {
      console.log(err);
      dispatch({
        type: "GET_ERRORS",
        payload: err.response.data
      });
    });
};
