import React, { Component } from "react";
import { connect } from "react-redux";
import { getAllBlogs } from "../Actions/blogActions";
import { loginUser } from "../Actions/authActions";

class Blogs extends Component {
  state = {
    blogs: null
  };

  componentDidMount() {
    if (localStorage.jwtToken) {
      let Object = localStorage.getItem("jwtToken");
      Object = JSON.parse(Object);
      // console.log(Object);

      const obj = {
        email: Object.email,
        password: Object.password
      };

      this.props.loginUser(obj);
    }
    this.props.getAllBlogs();
  }
  componentWillReceiveProps(next) {
    this.setState({ blogs: next.blog.blogs });
  }

  NotAuth = i => {
    if (this.state.blogs == null) {
      return <div>LOADING...</div>;
    } else {
      this.props.blog.blogs.map(blog => {
        console.log(blog);

        return (
          <div>
            {blog._id}
            <br />{" "}
          </div>
        );
      });
      // for (let i = 0; i < this.props.blog.blogs.length; i++) {
      //   return (
      //     <div className="container">
      //       {this.props.blog.blogs[i].blogContent}
      //       <br />
      //     </div>
      //   );
      // }
    }
  };
  render() {
    let { blogs } = this.props.blog;

    const blogStyle = {
      padding: "10px",
      border: "5px solid #d9534f",
      margin: "0"
    };

    return (
      <div className="container">
        <h1>All Blogs</h1>
        {this.NotAuth()}
        {this.props.blog.blogs.map(blog => {
          // console.log(blog);

          return (
            <div>
              <div style={blogStyle}>
                <div>{blog.blogContent} </div>
                <br />
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

const mapStatetoProps = state => ({
  // auth: state.auth,
  blog: state.blog,
  errors: state.errors
});

export default connect(
  mapStatetoProps,
  { getAllBlogs, loginUser }
)(Blogs);
