import axios from "axios";

export const newBlog = (blog, history) => dispatch => {
  axios
    .post("/blog", blog)
    .then(data => {
      history.push("/blogs");
    })
    .catch(err => {
      console.log(err);
      dispatch({
        type: "GET_ERRORS",
        payload: err.response.data
      });
    });
};

export const getAllBlogs = () => dispatch => {
  axios
    .get("/blog")
    .then(data => res.json(data))
    .catch(err => console.log(err));
};
